import React from 'react';
import { render } from 'react-dom';
import OrganismConfigPanel from './OrganismConfigPanel';
import './Organism.css';

type OrganismProps = {
    organismName: string,
    isSelected: boolean,
    remove: void | null,
    select: void | null,
}
export default class Organism extends React.Component<any, OrganismProps> {
    constructor(props:OrganismProps) {
        super(props);
        if (props) {
            const { organismName, isSelected, remove, select } = this.props;
            this.state = {
                organismName,
                isSelected,
                remove,
                select
            };
        } else {
            this.state = {
                organismName: '',
                isSelected: false,
                remove: null,
            };
        }
    }

    onMouseOver = () => {
        // console.log('! ! ! MOUSE OVER called on: ', this.state.organismName);
    }

    onMouseDown = () => {
        // console.log('! ! ! this: ', this);
        console.log('! ! ! onMousedown called on: ', this.state.organismName);
    }

    removeOrganism = () => {
        console.log('! ! ! remove organism called from organism! ! !');
        this.props.remove(this.state.organismName);
    }

    selectOrganism = () => {
        console.log('! ! ! select organism called from organism! ! !');
    }

    render (){
        return (
        <div className='organism'  onMouseDown={this.onMouseDown}>
            {this.props.organismName}<button className='remove' onClick={this.removeOrganism}>-</button>
        </div>
        )
    }
}

function removeOrganism() {
    console.log('! ! ! remove organism called');
}

export const FuncOrganism = ( organismName: string , index: number) => (
    <div className='organism' key={index}>
    {organismName}<button className='remove' onClick={removeOrganism}>-</button>
    </div>
)
