import React from 'react';
import './Organism.css';

type OrganismProps = {
    organismName: string,
    isSelected: boolean,
    remove: void | null,
    select: void | null,
}
export default class Organism extends React.Component<any, OrganismProps> {
    constructor(props:OrganismProps) {
        super(props);
        if (props) {
            const { organismName, isSelected, remove, select } = this.props;
            this.state = {
                organismName,
                isSelected,
                remove,
                select
            };
        } else {
            this.state = {
                organismName: '',
                isSelected: false,
                remove: null,
                select: null,
            };
        }
    }

    removeOrganism = () => {
        this.props.remove(this.state.organismName);
    }

    selectOrganism = () => {
        this.props.select(this.state.organismName);
        this.setState({
            isSelected: true
        })
    }

    render () {
        const { isSelected } = this.state;
        return (
            <div>
                {isSelected ? (
                    <div className='organism'  onMouseDown={this.selectOrganism}>
                    {this.props.organismName}<button className='remove' onClick={this.removeOrganism}>-</button>
                </div>
                ) : (
                <div className='organism'  onMouseDown={this.selectOrganism}>
                    {this.props.organismName}<button className='remove' onClick={this.removeOrganism}>-</button>
                </div>
                )}
            </div>
        )
    }
}
