import React from 'react';
import OrganismList from './OrganismList';

type ConfigPanelState = {
    organisms: Array<string>, 
    selectedOrganism: string,
    organismConfigs: {}, 
}
class ConfigPanel extends React.Component<{}, ConfigPanelState>{
    constructor(props: Readonly<{}>) {
        super(props);
        this.state = {
            organisms: [],
            selectedOrganism: '',
            organismConfigs: {},
        }
    }
    onChange = (event: { target: { value: string; }; }) => {
        this.setState({selectedOrganism: event.target.value});
        console.log('! ! ! this.state', this.state);
    }

    onSubmit = (event: { preventDefault: () => void; }) => {
        event.preventDefault()
        this.setState({
            selectedOrganism: '',
            organisms: [...this.state.organisms, this.state.selectedOrganism],
        });
    }
    render() {
        return (
            <div>
                <h1>This is the config Panel</h1>
                <form className="Todo" onSubmit={this.onSubmit}>
                    <input value={this.state.selectedOrganism} onChange={this.onChange} />
                    <button>Submit</button>
                </form>
                <OrganismList organisms={this.state.organisms} />
            </div>
        )
    }
}

export default ConfigPanel;
