import React from 'react';
import OrganismList from './OrganismList';

type OrganismConfigPanelProps = {
    organisms: Array<string>, 
    selectedOrganism: string,
    organismConfigs: {}, 
}


class OrganismConfigPanel extends React.Component<any, OrganismConfigPanelProps>{
    constructor(props: OrganismConfigPanelProps) {
        super(props);
        this.state = {
            organisms: [],
            selectedOrganism: '',
            organismConfigs: {},
        }
    }

    onChange = (event: { target: { value: string; }; }) => {
        this.setState({selectedOrganism: event.target.value});
        console.log('! ! ! this.state', this.state);
    }

    onSubmit = (event: { preventDefault: () => void; }) => {
        event.preventDefault()
        this.setState({
            selectedOrganism: '',
            organisms: [...this.state.organisms, this.state.selectedOrganism],
        });

    }

    shouldComponentUpdate() {
        console.log('should component update');
    }

    render() {
        console.log('! ! ! render called!');
        console.log('this.state: ', this.state);
        return (
            <div>
                <h1>This is the config Panel</h1>
                <form className="Todo" onSubmit={this.onSubmit}>
                    <input placeholder="Organism name" value={this.state.selectedOrganism} onChange={this.onChange} />
                    <button>Add</button>
                </form>
                <OrganismList organisms={this.state.organisms}/>
            </div>
        )
    }
}

export default OrganismConfigPanel;
